import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './AdminDashboard.css';

// ==================== Sidebar Component ====================
const Sidebar = ({ currentPage, onPageChange }) => {
  const menuItems = [
    { id: 'dashboard', icon: 'fa-house', title: 'Dashboard' },
    { id: 'users', icon: 'fa-id-card', title: 'RFID Users' },
    { id: 'bookings', icon: 'fa-calendar-check', title: 'Booking Requests' },
    { id: 'logs', icon: 'fa-clock-rotate-left', title: 'Access Logs' },
    { id: 'settings', icon: 'fa-gear', title: 'System Settings' }
  ];

  return (
    <aside className="sidebar">
      <div className="logo-box">
        <img
          src="/logo/enkku_logo.png"
          alt="Logo"
          onError={(e) => {
            // Fallback to SVG if PNG not found
            e.target.src = "/logo/enkku_logo.svg";
          }}
        />
      </div>

      <div className="sidebar-menu">
        {menuItems.map(item => (
          <a
            key={item.id}
            href="#"
            className={`menu-item ${currentPage === item.id ? 'active' : ''}`}
            title={item.title}
            onClick={(e) => {
              e.preventDefault();
              onPageChange(item.id);
            }}
          >
            <i className={`fa-solid ${item.icon}`}></i>
          </a>
        ))}
      </div>
    </aside>
  );
};

// ==================== Right Panel Component ====================
const RightPanel = ({ uuid, onFormSubmit, editingUser, onCancelEdit }) => {
  const [formData, setFormData] = useState({
    uuid: '',
    user_id: '',
    first_name: '',
    last_name: '',
    email: '',
    role: 'student'
  });
  const [searchUserId, setSearchUserId] = useState('');
  const [message, setMessage] = useState({ text: '', type: '' });

  // Update form when uuid changes (for adding new user)
  useEffect(() => {
    if (!editingUser) {
      setFormData(prev => ({ ...prev, uuid: uuid || '' }));
    }
  }, [uuid, editingUser]);

  // Update form when editingUser changes
  useEffect(() => {
    if (editingUser) {
      setFormData({
        uuid: editingUser.uuid || '',
        user_id: editingUser.user_id || '',
        first_name: editingUser.first_name || '',
        last_name: editingUser.last_name || '',
        email: editingUser.email || '',
        role: editingUser.role || 'student'
      });
    } else {
      // Reset form when not editing
      setFormData({
        uuid: uuid || '',
        user_id: '',
        first_name: '',
        last_name: '',
        email: '',
        role: 'student'
      });
    }
  }, [editingUser, uuid]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage({ text: '', type: '' });

    try {
      if (editingUser) {
        // Update existing user
        const response = await fetch(`/api/update_user/${editingUser.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });

        const data = await response.json();
        setMessage({
          text: data.message,
          type: data.success ? 'success' : 'error'
        });

        if (data.success) {
          setTimeout(() => {
            if (onCancelEdit) onCancelEdit();
            if (onFormSubmit) onFormSubmit();
          }, 1000);
        }
      } else {
        // Add new user
        const response = await fetch('/api/add_user', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });

        const data = await response.json();
        setMessage({
          text: data.message,
          type: data.success ? 'success' : 'error'
        });

        if (data.success) {
          setFormData({
            uuid: '',
            user_id: '',
            first_name: '',
            last_name: '',
            email: '',
            role: 'student'
          });
          setTimeout(() => {
            if (onFormSubmit) onFormSubmit();
          }, 1000);
        }
      }
    } catch (error) {
      setMessage({ text: 'Error saving user', type: 'error' });
    }
  };

  const handleSearch = async () => {
    if (!searchUserId.trim()) return;

    try {
      const response = await fetch('/api/users');
      const data = await response.json();
      const user = data.users.find(
        u => u.user_id === searchUserId.trim()
      );

      if (user) {
        setFormData({
          uuid: user.uuid || '',
          user_id: user.user_id || '',
          first_name: user.first_name || '',
          last_name: user.last_name || '',
          email: user.email || '',
          role: user.role || 'student'
        });
      } else {
        alert('ไม่พบ User ID นี้');
      }
    } catch (error) {
      alert('Error searching user');
    }
  };

  const handleSearchKey = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSearch();
    }
  };

  const handleCancel = () => {
    setMessage({ text: '', type: '' });
    if (onCancelEdit) onCancelEdit();
  };

  return (
    <aside className="right-panel">
      <div className="search-box">
        <i className="fa fa-search"></i>
        <input
          type="text"
          placeholder="Search User ID"
          value={searchUserId}
          onChange={(e) => setSearchUserId(e.target.value)}
          onKeyDown={handleSearchKey}
        />
      </div>

      <h3>{editingUser ? 'Edit User' : 'Add User'}</h3>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="uuid"
          placeholder="RFID"
          value={formData.uuid}
          readOnly
          required
        />
        <input
          type="text"
          name="user_id"
          placeholder="User ID"
          value={formData.user_id}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="first_name"
          placeholder="First Name"
          value={formData.first_name}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="last_name"
          placeholder="Last Name"
          value={formData.last_name}
          onChange={handleInputChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleInputChange}
          required
        />
        <select
          name="role"
          value={formData.role}
          onChange={handleInputChange}
        >
          <option value="student">Student</option>
          <option value="admin">Admin</option>
        </select>

        {editingUser ? (
          <>
            <button type="submit">Update User</button>
            <button type="button" onClick={handleCancel}>Cancel</button>
          </>
        ) : (
          <button type="submit">Add RFID</button>
        )}
      </form>

      {message.text && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}
    </aside>
  );
};

