// ==================== Main Admin Dashboard Component ====================
const AdminDashboard = () => {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [uuid, setUuid] = useState('');
  const [refreshKey, setRefreshKey] = useState(0);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    // Initialize Socket.IO
    const socket = io({ transports: ['websocket', 'polling'] });

    // Reset UUID on load
    fetch('/api/reset_uuid', { method: 'POST' }).catch(err => {
      console.error('Error resetting UUID:', err);
    });

    // Listen for UUID updates
    socket.on('uuid_update', (data) => {
      setUuid(data.uuid || '');
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleFormSubmit = () => {
    setRefreshKey(prev => prev + 1);
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setCurrentPage('users'); // Ensure we're on the users page
  };

  const handleCancelEdit = () => {
    setEditingUser(null);
  };

  const renderContent = () => {
    switch (currentPage) {
      case 'dashboard':
        return <DashboardContent />;
      case 'users':
        return <UsersTable onRefresh={refreshKey} onEditUser={handleEditUser} />;
      case 'settings':
        return <SystemSettings />;
      case 'bookings':
        return <div className="page-title-box"><span className="page-title">Booking Requests (Coming Soon)</span></div>;
      case 'logs':
        return <div className="page-title-box"><span className="page-title">Access Logs (Coming Soon)</span></div>;
      default:
        return <DashboardContent />;
    }
  };

  return (
    <div className="layout">
      <Sidebar currentPage={currentPage} onPageChange={setCurrentPage} />

      <main className="main">
        <div id="main-content">
          {renderContent()}
        </div>
      </main>

      <RightPanel 
        uuid={uuid} 
        onFormSubmit={handleFormSubmit}
        editingUser={editingUser}
        onCancelEdit={handleCancelEdit}
      />
    </div>
  );
};

export default AdminDashboard;
