import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './AdminDashboard.css';
import BookingsPage from './Bookingspage';

// ==================== Sidebar Component ====================
const Sidebar = ({ currentPage, onPageChange, onLogout, user }) => {
  const menuItems = [
    { id: 'dashboard', icon: 'fa-house', title: 'Dashboard' },
    { id: 'users', icon: 'fa-id-card', title: 'RFID Users' },
    { id: 'bookings', icon: 'fa-calendar-check', title: 'Booking Requests' },
    { id: 'logs', icon: 'fa-clock-rotate-left', title: 'Access Logs' },
    { id: 'settings', icon: 'fa-gear', title: 'System Settings' }
  ];

  return (
    <aside className="sidebar">
      <div className="logo-box">
        <img
          src="/logo/enkku_logo.png"
          alt="Logo"
          onError={(e) => {
            e.target.src = "/logo/enkku_logo.svg";
          }}
        />
      </div>

      <div className="sidebar-menu">
        {menuItems.map(item => (
          <a
            key={item.id}
            href="#"
            className={`menu-item ${currentPage === item.id ? 'active' : ''}`}
            title={item.title}
            onClick={(e) => {
              e.preventDefault();
              onPageChange(item.id);
            }}
          >
            <i className={`fa-solid ${item.icon}`}></i>
          </a>
        ))}

        {/* Logout Button */}
        <a
          href="#"
          className="menu-item logout-item"
          title="Logout"
          onClick={(e) => {
            e.preventDefault();
            if (window.confirm('Are you sure you want to logout?')) {
              onLogout();
            }
          }}
        >
          <i className="fa-solid fa-right-from-bracket"></i>
        </a>
      </div>

      {/* User Info at Bottom */}
      {user && (
        <div className="sidebar-user">
          <div className="user-avatar">
            {user.first_name?.[0]}{user.last_name?.[0]}
          </div>
          <div className="user-name">{user.first_name}</div>
        </div>
      )}
    </aside>
  );
};


// ==================== Right Panel Component ====================
const RightPanel = ({ uuid, onFormSubmit, editingUser, onCancelEdit }) => {
  const [formData, setFormData] = useState({
    uuid: '',
    user_id: '',
    first_name: '',
    last_name: '',
    email: '',
    role: 'student'
  });
  const [searchUserId, setSearchUserId] = useState('');
  const [message, setMessage] = useState({ text: '', type: '' });

  // Update form when uuid changes (for adding new user)
  useEffect(() => {
    if (!editingUser) {
      setFormData(prev => ({ ...prev, uuid: uuid || '' }));
    }
  }, [uuid, editingUser]);

  // Update form when editingUser changes
  useEffect(() => {
    if (editingUser) {
      setFormData({
        uuid: editingUser.uuid || '',
        user_id: editingUser.user_id || '',
        first_name: editingUser.first_name || '',
        last_name: editingUser.last_name || '',
        email: editingUser.email || '',
        role: editingUser.role || 'student'
      });
    } else {
      // Reset form when not editing
      setFormData({
        uuid: uuid || '',
        user_id: '',
        first_name: '',
        last_name: '',
        email: '',
        role: 'student'
      });
    }
  }, [editingUser, uuid]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage({ text: '', type: '' });

    try {
      if (editingUser) {
        // Update existing user
        const response = await fetch(`/api/update_user/${editingUser.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });

        const data = await response.json();
        setMessage({
          text: data.message,
          type: data.success ? 'success' : 'error'
        });

        if (data.success) {
          // Auto-hide success message after 2 seconds
          setTimeout(() => {
            setMessage({ text: '', type: '' });
            if (onCancelEdit) onCancelEdit();
            if (onFormSubmit) onFormSubmit();
          }, 2000);
        }
      } else {
        // Add new user
        const response = await fetch('/api/add_user', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });

        const data = await response.json();
        setMessage({
          text: data.message,
          type: data.success ? 'success' : 'error'
        });

        if (data.success) {
          setFormData({
            uuid: '',
            user_id: '',
            first_name: '',
            last_name: '',
            email: '',
            role: 'student'
          });
          // Auto-hide success message after 2 seconds
          setTimeout(() => {
            setMessage({ text: '', type: '' });
            if (onFormSubmit) onFormSubmit();
          }, 2000);
        }
      }
    } catch (error) {
      setMessage({ text: 'Error saving user', type: 'error' });
    }
  };

  const handleSearch = async () => {
    if (!searchUserId.trim()) return;

    try {
      const response = await fetch('/api/users');
      const data = await response.json();
      const user = data.users.find(
        u => u.user_id === searchUserId.trim()
      );

      if (user) {
        setFormData({
          uuid: user.uuid || '',
          user_id: user.user_id || '',
          first_name: user.first_name || '',
          last_name: user.last_name || '',
          email: user.email || '',
          role: user.role || 'student'
        });
      } else {
        alert('ไม่พบ User ID นี้');
      }
    } catch (error) {
      alert('Error searching user');
    }
  };

  const handleSearchKey = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSearch();
    }
  };

  const handleCancel = () => {
    setMessage({ text: '', type: '' });
    if (onCancelEdit) onCancelEdit();
  };

  return (
    <aside className="right-panel">
      <div className="search-box">
        <i className="fa fa-search"></i>
        <input
          type="text"
          placeholder="Search User ID"
          value={searchUserId}
          onChange={(e) => setSearchUserId(e.target.value)}
          onKeyDown={handleSearchKey}
        />
      </div>

      <h3>{editingUser ? 'Edit User' : 'Add User'}</h3>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="uuid"
          placeholder="RFID"
          value={formData.uuid}
          readOnly
          required
        />
        <input
          type="text"
          name="user_id"
          placeholder="User ID"
          value={formData.user_id}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="first_name"
          placeholder="First Name"
          value={formData.first_name}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="last_name"
          placeholder="Last Name"
          value={formData.last_name}
          onChange={handleInputChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleInputChange}
          required
        />

        {/* Custom Role Selector */}
        <div className="role-selector">
          <label>Role:</label>
          <div className="role-options">
            <button
              type="button"
              className={`role-btn ${formData.role === 'student' ? 'active' : ''}`}
              onClick={() => setFormData(prev => ({ ...prev, role: 'student' }))}
            >
              <i className="fa-solid fa-user-graduate"></i>
              <span>Student</span>
            </button>
            <button
              type="button"
              className={`role-btn ${formData.role === 'admin' ? 'active' : ''}`}
              onClick={() => setFormData(prev => ({ ...prev, role: 'admin' }))}
            >
              <i className="fa-solid fa-user-shield"></i>
              <span>Admin</span>
            </button>
          </div>
        </div>

        {editingUser ? (
          <>
            <button type="submit">Update User</button>
            <button type="button" onClick={handleCancel}>Cancel</button>
          </>
        ) : (
          <button type="submit">Add RFID</button>
        )}
      </form>

      {message.text && (
        <div className={`message ${message.type}`}>
          {message.text}
        </div>
      )}
    </aside>
  );
};

// ==================== Dashboard Content Component ====================
const DashboardContent = () => {
  const [stats, setStats] = useState({
    totalBooking: 0,
    bookingRequest: 0,
    registerRequest: 0
  });
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch bookings
      const bookingResponse = await fetch('/api/bookings/all', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (bookingResponse.ok) {
        const bookingData = await bookingResponse.json();
        const allBookings = bookingData.bookings || [];
        
        // Filter only pending bookings for dashboard
        const pendingBookings = allBookings.filter(b => b.status === 'pending');
        setBookings(pendingBookings.slice(0, 10)); // Show only first 10

        // Calculate stats
        setStats({
          totalBooking: bookingRequest + pendingBookings,
          bookingRequest: pendingBookings.length,
          registerRequest: 0 // Can be updated based on other API
        });
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAccept = async (bookingId) => {
    const remark = prompt('หมายเหตุ (ถ้ามี):');
    if (remark === null) return;

    try {
      const response = await fetch(`/api/bookings/${bookingId}/approve`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ remark })
      });

      const data = await response.json();
      
      if (response.ok) {
        alert('อนุมัติการจองสำเร็จ');
        fetchDashboardData(); // Refresh data
      } else {
        alert(data.message || 'เกิดข้อผิดพลาด');
      }
    } catch (error) {
      console.error('Error approving booking:', error);
      alert('เกิดข้อผิดพลาดในการเชื่อมต่อ');
    }
  };

  const handleDecline = async (bookingId) => {
    const remark = prompt('เหตุผลในการปฏิเสธ:');
    if (!remark) return;

    try {
      const response = await fetch(`/api/bookings/${bookingId}/reject`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ remark })
      });

      const data = await response.json();
      
      if (response.ok) {
        alert('ปฏิเสธการจองสำเร็จ');
        fetchDashboardData(); // Refresh data
      } else {
        alert(data.message || 'เกิดข้อผิดพลาด');
      }
    } catch (error) {
      console.error('Error rejecting booking:', error);
      alert('เกิดข้อผิดพลาดในการเชื่อมต่อ');
    }
  };

  if (loading) {
    return (
      <div style={{ textAlign: 'center', padding: '40px', color: '#fff' }}>
        <i className="fas fa-spinner fa-spin" style={{ fontSize: '32px' }}></i>
        <p>กำลังโหลดข้อมูล...</p>
      </div>
    );
  }

  return (
    <div>
      <div className="page-title-box">
        <span className="page-title">Admin Dashboard</span>
      </div>

      <div className="quick-stats-title">Quick Stats</div>

      <div className="stats">
        <div className="stat-card">
          <div className="label">Total Booking</div>
          <b>{stats.totalBooking}</b>
        </div>
        <div className="stat-card">
          <div className="label">Booking Request</div>
          <b>{stats.bookingRequest}</b>
        </div>
        <div className="stat-card">
          <div className="label">Register Request</div>
          <b>{stats.registerRequest}</b>
        </div>
      </div>

      <div className="bookings-title">Recent Booking Requests</div>

      <div className="container">
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>ผู้จอง</th>
                <th>Room</th>
                <th>Date</th>
                <th>Time</th>
                <th>Detail</th>
                <th>Decision</th>
              </tr>
            </thead>
            <tbody>
              {bookings.length === 0 ? (
                <tr>
                  <td colSpan="6" style={{textAlign: 'center', padding: '20px'}}>
                    ไม่มีคำขอจองใหม่
                  </td>
                </tr>
              ) : (
                bookings.map((booking) => (
                  <tr key={booking.id}>
                    <td>{booking.user_name || booking.user_email}</td>
                    <td>{booking.room}</td>
                    <td>{booking.date}</td>
                    <td>{booking.start_time} - {booking.end_time}</td>
                    <td>{booking.detail || '-'}</td>
                    <td>
                      <span
                        className="btn-accept"
                        onClick={() => handleAccept(booking.id)}
                      >
                        Accept
                      </span>
                      {' | '}
                      <span
                        className="btn-decline"
                        onClick={() => handleDecline(booking.id)}
                      >
                        Decline
                      </span>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

// ==================== Users Table Component ====================
const UsersTable = ({ onRefresh, onEditUser }) => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadUsers = async () => {
    try {
      const response = await fetch('/api/users');
      const data = await response.json();
      setUsers(data.users || []);
    } catch (error) {
      console.error('Error loading users:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadUsers();
  }, [onRefresh]);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this user?')) {
      return;
    }

    try {
      const response = await fetch(`/api/delete_user/${id}`, {
        method: 'DELETE'
      });
      const data = await response.json();

      if (data.success) {
        loadUsers();
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert('Error deleting user');
    }
  };

  const handleEdit = async (userId) => {
    try {
      const response = await fetch(`/api/user/${userId}`);
      const user = await response.json();

      if (user.error) {
        alert('Failed to load user data');
        return;
      }

      if (onEditUser) {
        onEditUser(user);
      }
    } catch (error) {
      alert('Error loading user data');
    }
  };

  const adminCount = users.filter(u => u.role === 'admin').length;
  const studentCount = users.filter(u => u.role === 'student').length;

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <div className="page-title-box">
        <span className="page-title">RFID Registered Users</span>
      </div>

      <div className="quick-stats-title">Quick Stats</div>

      <div className="stats">
        <div className="stat-card">
          <div className="label">Total Users</div>
          <b>{users.length}</b>
        </div>
        <div className="stat-card">
          <div className="label">Admins</div>
          <b>{adminCount}</b>
        </div>
        <div className="stat-card">
          <div className="label">Users</div>
          <b>{studentCount}</b>
        </div>
      </div>

      <div className="bookings-title">Users</div>

      <div className="container">
        {users.length > 0 ? (
          <>
            {/* Desktop Table View */}
            <div className="table-container">
              <table>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>UUID</th>
                    <th>User ID</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Role</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map(user => (
                    <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>{user.uuid}</td>
                      <td>{user.user_id}</td>
                      <td>{user.first_name}</td>
                      <td>{user.last_name}</td>
                      <td>{user.email}</td>
                      <td>{user.role}</td>
                      <td>
                        <div className="action-buttons">
                          <span
                            className="action-link edit"
                            onClick={() => handleEdit(user.id)}
                          >
                            Edit
                          </span>
                          <span
                            className="action-link delete"
                            onClick={() => handleDelete(user.id)}
                          >
                            Delete
                          </span>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Mobile Card View */}
            <div className="mobile-cards">
              {users.map(user => (
                <div key={user.id} className="user-card">
                  <div className="card-header">
                    <div className="card-id">ID: {user.id}</div>
                    <div className="card-actions">
                      <span
                        className="action-link edit"
                        onClick={() => handleEdit(user.id)}
                      >
                        Edit
                      </span>
                      <span
                        className="action-link delete"
                        onClick={() => handleDelete(user.id)}
                      >
                        Delete
                      </span>
                    </div>
                  </div>

                  <div className="card-field">
                    <div className="field-label">UUID:</div>
                    <div className="field-value">{user.uuid}</div>
                  </div>

                  <div className="card-field">
                    <div className="field-label">User ID:</div>
                    <div className="field-value">{user.user_id}</div>
                  </div>

                  <div className="card-field">
                    <div className="field-label">Name:</div>
                    <div className="field-value">
                      {user.first_name} {user.last_name}
                    </div>
                  </div>

                  <div className="card-field">
                    <div className="field-label">Email:</div>
                    <div className="field-value">{user.email}</div>
                  </div>

                  <div className="card-field">
                    <div className="field-label">Role:</div>
                    <div className="field-value">{user.role}</div>
                  </div>
                </div>
              ))}
            </div>
          </>
        ) : (
          <div className="no-users">
            <p>No users found.</p>
          </div>
        )}
      </div>
    </div>
  );
};

// ==================== System Settings Component ====================
const SystemSettings = () => {
  const [doorStatus, setDoorStatus] = useState('LOCKED');

  const openDoor = async () => {
    try {
      const response = await fetch('/api/door/open', { method: 'POST' });
      const data = await response.json();
      alert(data.message);
      setDoorStatus('OPEN');
    } catch (error) {
      alert('Error opening door');
    }
  };

  const closeDoor = async () => {
    try {
      const response = await fetch('/api/door/close', { method: 'POST' });
      const data = await response.json();
      alert(data.message);
      setDoorStatus('LOCKED');
    } catch (error) {
      alert('Error closing door');
    }
  };

  return (
    <div>
      <div className="page-title-box">
        <div className="page-title">System Settings</div>
      </div>

      <div className="door-stats">
        <div className="stat-card">
          <div className="label">Status</div>
          <div className={`door-status ${doorStatus.toLowerCase()}`}>
            {doorStatus}
          </div>

          <div className="door-actions">
            <button className="door-btn open" onClick={openDoor}>
              OPEN
            </button>
            <button className="door-btn close" onClick={closeDoor}>
              CLOSE
            </button>
          </div>
        </div>

        <div className="stat-card empty"></div>
        <div className="stat-card empty"></div>
      </div>
    </div>
  );
};

// ==================== Main Admin Dashboard Component ====================
const AdminDashboard = ({ user, onLogout }) => {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [uuid, setUuid] = useState('');
  const [refreshKey, setRefreshKey] = useState(0);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    // Initialize Socket.IO
    const socket = io({ transports: ['websocket', 'polling'] });

    // Reset UUID on load
    fetch('/api/reset_uuid', { method: 'POST' }).catch(err => {
      console.error('Error resetting UUID:', err);
    });

    // Listen for UUID updates
    socket.on('uuid_update', (data) => {
      setUuid(data.uuid || '');
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleFormSubmit = () => {
    setRefreshKey(prev => prev + 1);
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setCurrentPage('users');
  };

  const handleCancelEdit = () => {
    setEditingUser(null);
  };

  const renderContent = () => {
    switch (currentPage) {
      case 'dashboard':
        return <DashboardContent />;
      case 'users':
        return <UsersTable onRefresh={refreshKey} onEditUser={handleEditUser} />;
      case 'settings':
        return <SystemSettings />;
      case 'bookings':
        return <BookingsPage />;
      case 'logs':
        return <div className="page-title-box"><span className="page-title">Access Logs (Coming Soon)</span></div>;
      default:
        return <DashboardContent />;
    }
  };

  return (
    <div className="layout">
      <Sidebar
        currentPage={currentPage}
        onPageChange={setCurrentPage}
        onLogout={onLogout}
        user={user}
      />

      <main className="main">
        <div id="main-content">
          {/* Welcome Header */}
          <div className="welcome-header">
            <h1>Welcome, {user?.first_name} {user?.last_name}</h1>
            <p className="user-role">{user?.role || 'Admin'}</p>
          </div>

          {renderContent()}
        </div>
      </main>

      <RightPanel
        uuid={uuid}
        onFormSubmit={handleFormSubmit}
        editingUser={editingUser}
        onCancelEdit={handleCancelEdit}
      />
    </div>
  );
};

export default AdminDashboard;
